      lv_anim_set_values(&anim_y, lv_obj_get_y_aligned(img), y_target);
      lv_anim_set_time(&anim_y, 3000); // 1000 + lv_rand(0, 2000));
      lv_anim_set_exec_cb(&anim_y, (lv_anim_exec_xcb_t)lv_obj_set_y);
