      case 0x0F:
            return 7;
      case 0x10:
            return 8;
      case 0x11:
            return 9;
      case 0x12:
            return 10;
      case 0x13:
            return 11;
      case 0x14:
            return 12;
      case 0x16:
            return 13;
      case 0x17:
            return 14;
      case 0x18:
            return 15;
      case 0xC0:
            return 16;
      case 0x09:
            return 17;
      default:
